define([
  'controllers/base/controller',
  'models/feed/feed-model',
  'views/feed/feed-view'

], function(Controller, FeedModel, FeedView) {
  'use strict';

  var FeedController = Controller.extend({
    initialize: function(){

    },

    // Actions
    index: function(params) {
      this.model = new FeedModel();

      this.view = new FeedView ({
          model: this.model,
          region:'content'
      });      

      this.model.fetch().then(function() { this.view.render() }.bind(this));
    }
  });
  
  return FeedController;
});