define(function() {
  'use strict';

  // The routes for the application. This module returns a function.
  // `match` is match method of the Router
  return function(match) {
    match('', 'authorization#index');
    match('users', 'users#index');
    match('users/:id', 'users#index');
    match('me/show', 'me#show');
    match('me/change', 'me#change');
    match('feed','feed#index');
    match('register','register#index')
    match('me/followers', 'me#followers')
    match('me/following', 'me#following')
  };
});
