define([
  'views/base/view',
  'text!templates/me/me-show-view.hbs',
], function(View, templateShow) {
  'use strict';

	var MeShowView = View.extend({
	    autoRender: true,

	    template: templateShow,

	    events: {
	    	"click #delete":"deleteProfile"
	    },

	    deleteProfile: function() {
	        $.ajax({url:"/me",
	          method:"DELETE",
	          success: function() {
	            	config.nick="";
	            	location.hash="";
	          }});
		    }
		});
	
  	return MeShowView;
});